/* global AMap */
import {
  createElement, useRef, createRef, forwardRef, useImperativeHandle
} from 'rax';

import cloneElement from 'rax-clone-element';
import View from 'rax-view';
import Slider from 'rax-slider';

const baseImgR = 'data:image/png;base64,';
const baseImgL = 'data:image/png;base64,';

const itemStyle = {
  backgroundColor: 'white',
  textAlign: 'center',
  width: '60%',
  border: '1px solid #ccc',
  borderRadius: 20,
  boxShadow: '1px 1px 10px 0 #ccc',
  fontSize: 6,
  paddingTop: 1,
  paddingBottom: 1,
  // pointerEvents: 'none',
  webkitUserSelect: 'none',
  userSelect: 'none'
};

const viewStyle = {
  overflow: 'hidden',
  position: 'absolute',
  width: '100%',
  bottom: 0,
  zIndex: 999,
  paddingTop: 2,
  paddingBottom: 2,
  justifyContent: 'center',
  flexDirection: 'row'
};

const arrowStyleL = {
  width: 10,
  height: 10,
  lineHeight: 10,
  zIndex: 999,
  textAlign: 'center',
  justifyContent: 'center',
  border: '1px solid #ccc',
  borderRadius: 10,
  backgroundColor: 'white',
  boxShadow: '1px 1px 10px 0 #ccc',
  marginLeft: 10,
  webkitUserSelect: 'none',
  userSelect: 'none'
};

const arrowStyleR = {
  width: 10,
  height: 10,
  lineHeight: 10,
  zIndex: 999,
  textAlign: 'center',
  justifyContent: 'center',
  border: '1px solid #ccc',
  borderRadius: 10,
  backgroundColor: 'white',
  boxShadow: '1px 1px 10px 0 #ccc',
  marginRight: 10,
  webkitUserSelect: 'none',
  userSelect: 'none'
};

const arrowStyle = {
  position: 'absolute',
  flexDirection: 'row',
  alignContent: 'center',
  width: '100%',
  justifyContent: 'space-between'
};

const itemContainer = {
  justifyContent: 'center',
  flexDirection: 'row'
};

let scope = null;

const DetailSwiper = (props, ref) => {
  let currentIndex = 0;
  const sliderRef = createRef(null);

  const map = props.__map__;
  const mapStore = props.__ele__;

  const render = props.render; // 底部 card 的渲染样式
  const loop = props.loop || true; // card 是否支持轮播
  const autoPlay = props.autoPlay || false; // 是否支持自动播放
  const arrowRender = props.arrowRender;// 箭头的渲染样式
  const animationEndHanler = props.animationEndHanler; // 底部 `card` 每次滑动效果结束触发的事件

  const currentZoom = map.getZoom();

  const getChildren = () => {
    let list = [];
    if (map) {
      return map.getAllOverlays('marker');
    }
  };

  function slideTo(index) {
    sliderRef.current && sliderRef.current.slideTo(index);
  }

  useImperativeHandle(ref, () => ({
    slideTo: (activeItem) => {
      const children = getChildren();
      const index = (children.findIndex((item) => {
        const pos = item.getPosition();
        const activePos = activeItem.getPosition();
        return String(pos.lng) === String(activePos.lng) && String(pos.lat) === String(activePos.lat);
      }) || 0);
      slideTo(index);
    }
  }));

  const markerGoCenter = (index) => {
    setTimeout(() => {
      const children = getChildren();
      // const index = scope.state.index;
      const marker = children[index];
      if (children && marker) {
        const positon = marker.getPosition();
        map.setCenter(positon);
      }
    }, 0);
  };

  const arrowHandler = (dir) => {
    if (dir === 'left') {
      currentIndex--;
      if (currentIndex < 0) currentIndex = getChildren().length - 1;
      //scope.pre();
    } else {
      //scope.next();
      currentIndex++;
      if (currentIndex >= getChildren().length) currentIndex = 0;
    }
    //console.log('handlerChange======',dir,currentIndex,getChildren().length);
    slideTo(currentIndex);
    markerGoCenter(currentIndex);
  };

  const handlerChange = ({index}) => {
    currentIndex = index;
    animationEndHanler(index);
  };

  if (!map) {
    console.log('组件必须作为 Map 的子组件使用');
    return;
  } else {
    if (!window.changeSwiperLabel) {
      window.changeSwiperLabel = () => {
        setTimeout(() => {
          const mapCenter = map.getCenter();
          const children = getChildren();
          const index = children.findIndex((item) => {
            const pos = item.getPosition();
            return String(pos.lng) === String(mapCenter.lng) && String(pos.lat) === String(mapCenter.lat);
          });
          if (scope && index >= 0) {
            scope.setMyState(index, true);
          }
        }, 0);
      };
      map.on('moveend', window.changeSwiperLabel);
    }

    // if(window.AMap){
    //   window.AMap.event.removeListener("moveend");
    //   window.AMap.event.addListenerOnce(map, "moveend", changeSwiperLabel);
    // }
  }

  return (
    <View id={'@slider-container@'} style={viewStyle}>
      <View style={arrowStyle}>
        {
          arrowRender instanceof Function ? arrowRender('left') :
            <View style={{ justifyContent: 'center' }} onClick={arrowHandler.bind(this, 'left')}>
              <View style={arrowStyleL}> {'◀'} </View>
            </View>
        }
        {
          arrowRender instanceof Function ? arrowRender('right') :
            <View style={{ justifyContent: 'center' }} onClick={arrowHandler.bind(this, 'right')}>
              <View style={arrowStyleR}> {'▶'} </View>
            </View>
        }
      </View>
      <Slider ref={sliderRef} width="100vw" height="12vh" loop={loop} autoPlay={autoPlay} onChange={handlerChange}>
        {
          getChildren().map((item, index) => {
            //console.log('ok=>', item.getExtData());
            return (
              render instanceof Function ?
                render() :
                <View style={itemContainer}>
                  <View id={'@slider-item-' + index + '@'} key={'slider-item-' + index} style={itemStyle}>
                    {item.getExtData().cacheIndex}:{item.getExtData().myLabel}
                  </View>
                </View>
            );
          })
        }
      </Slider>
    </View>
  );
}
  ;

export default forwardRef(DetailSwiper);